public class Product {		private String name;	private int count;	private double price;		public Product(String n, double p, int c) {		name = n;		price = p;		count = c;	}		public Product(String n, double p) {		name = n;		price = p;	}		public String getName() {		return name;	}		public void setName(String n) {		name = n;	}		public int getCount() {		return count;	}		public void setCount(int c) {		count = c;	}		public double getPrice() {		return price;	}		public void setPrice(double p) {		price = p;	}		public String toString() {		return "Name: " + name + "\nPrice: " + price + "\nCount: " + count;	}}